import React from 'react';
import { Nav } from 'react-bootstrap';

const Sidebar = () => {
  const handleTabChange = (tab) => {
    window.dispatchEvent(new CustomEvent('sidebarTabChange', { detail: tab }));
  };

  return (
    <div className="d-flex flex-column border-end vh-100" style={{ width: '240px' }}>
      <div className="text-success fw-bold fs-3 text-center py-4 border-bottom">
        SCOR
      </div>
      <Nav className="flex-column px-3 mt-3">
        <Nav.Link
          className="text-success fw-semibold fs-5 py-2"
          onClick={() => handleTabChange('programs')}
        >
          Programs
        </Nav.Link>
        <Nav.Link
          className="text-success fw-semibold fs-5 py-2"
          onClick={() => handleTabChange('schedules')}
        >
          Schedules
        </Nav.Link>
      </Nav>
    </div>
  );
};

export default Sidebar;
