import React from 'react';
import { Button } from 'react-bootstrap';

const Header = ({ onSchedulesClick }) => (
  <header style={{ height: 72, background: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0 32px', boxShadow: '0 2px 8px 0 rgba(40,167,69,0.06)' }}>
    <div style={{ fontWeight: 800, fontSize: '1.5rem', color: '#28a745' }}>Training Dashboard</div>
    <div>
      <Button variant="outline-success" style={{ fontWeight: 600, borderRadius: 24, marginRight: 12 }}>Profile</Button>
      <Button variant="outline-info" style={{ fontWeight: 600, borderRadius: 24, marginRight: 12 }} onClick={onSchedulesClick}>
        <i className="bi bi-calendar-check me-2"></i>Schedules
      </Button>
      <Button variant="outline-danger" style={{ fontWeight: 600, borderRadius: 24 }}>Logout</Button>
    </div>
  </header>
);

export default Header;
