import React, { useEffect, useState } from 'react';
import { Card, Table, Button, Spinner, Alert, Badge } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_BASE = 'http://localhost:5000/api';

  // Use palette and styles similar to admin portal dashboard
  const palette = {
    card: '#f8fff6',
    section: '#e9fbe5',
    primary: '#28a745',
    border: '#b6e6a2',
    text: '#1a4d1a',
    danger: '#dc3545',
  };
  const cardStyle = {
    borderRadius: 0,
    background: palette.card,
    color: palette.text,
    fontFamily: 'Inter, Arial, sans-serif',
    boxShadow: '0 4px 24px rgba(40,167,69,0.10)',
    border: 'none',
  };
  const gradientHeader = {
    background: palette.card,
    color: palette.primary,
    fontWeight: 700,
    fontSize: '1.4rem',
    letterSpacing: 1,
    padding: '18px 24px',
    border: 'none',
    borderRadius: 0,
  };

function EmployeeDashboard() {
  const email = localStorage.getItem('employeeEmail');
  const [schedules, setSchedules] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showAgent, setShowAgent] = useState(false);
  const [activeSchedule, setActiveSchedule] = useState(null);

  useEffect(() => {
    if (email) fetchSchedules();
  }, [email]);

  const fetchSchedules = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await fetch(`${API_BASE}/login?email=${encodeURIComponent(email)}`);
      if (!res.ok) throw new Error('Failed to load schedules');
      const data = await res.json();
      setSchedules(Array.isArray(data) ? data : []);
    } catch (err) {
      setError(err.message || 'Failed to load schedules');
      setSchedules([]);
    } finally {
      setLoading(false);
    }
  };

  const handleStartOrResume = (schedule) => {
    setActiveSchedule(schedule);
    setShowAgent(true);
  };

  return (
    <div className="container py-4" style={{ background: palette.section, minHeight: '100vh' }}>
      <Card className="mb-4 shadow-lg" style={cardStyle}>
        <Card.Header style={gradientHeader}>
          <h1 className="mb-0 text-secondary" style={{ fontWeight: 700, color: palette.primary }}>Employee Training Dashboard</h1>
        </Card.Header>
        <Card.Body style={{ background: palette.card }}>
          {loading ? (
            <Spinner animation="border" />
          ) : error ? (
            <Alert variant="danger">{error}</Alert>
          ) : schedules.length === 0 ? (
            <Alert variant="info">No schedules assigned to this employee.</Alert>
          ) : (
            <Table striped bordered hover responsive className="shadow-sm" style={{ borderRadius: 0, overflow: 'hidden', background: '#e9fbe5', fontFamily: cardStyle.fontFamily, color: '#1a4d1a' }}>
              <thead style={{ background: '#2ecc40', color: '#fff', fontSize: '1.15rem', letterSpacing: 1 }}>
                <tr>
                  <th style={{ fontWeight: 800, border: 'none' }}>Program</th>
                  <th style={{ fontWeight: 800, border: 'none' }}>Start Date</th>
                  <th style={{ fontWeight: 800, border: 'none' }}>End Date</th>
                  <th style={{ fontWeight: 800, border: 'none' }}>Status</th>
                  <th style={{ fontWeight: 800, border: 'none' }}>Action</th>
                </tr>
              </thead>
              <tbody>
                {schedules.map(s => (
                  <tr key={s.id} style={{ ...cardStyle, background: '#28a745', color: '#1a4d1a' }}>
                    <td>
                      <span
                        style={{ fontWeight: 500, color: '#28a745', cursor: 'pointer' }}
                        onClick={() => handleStartOrResume(s)}
                      >{s.program_title || s.program_id}</span>
                    </td>
                    <td>{s.start_date}</td>
                    <td>{s.end_date}</td>
                    <td>
                      {s.is_finished ? (
                        <Badge bg="success" style={{ fontWeight: 600, fontSize: 13, borderRadius: 8, padding: '2px 10px', border: 'none', fontFamily: cardStyle.fontFamily, background: '#2ecc40', color: '#fff' }}>Completed</Badge>
                      ) : (
                        <Badge bg="warning" style={{ fontWeight: 600, fontSize: 13, borderRadius: 8, padding: '2px 10px', border: 'none', fontFamily: cardStyle.fontFamily, background: '#b6e6a2', color: '#fff' }}>Pending</Badge>
                      )}
                    </td>
                    <td>
                      <Button
                        variant="success"
                        style={{ borderRadius: 8, fontWeight: 600, fontFamily: cardStyle.fontFamily, color: '#2ecc40', borderColor: '#2ecc40' }}
                        onClick={() => handleStartOrResume(s)}
                      >
                        {s.is_started ? 'Resume' : 'Start'}
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Card.Body>
      </Card>
      {/* Onboarding Agent */}
      {showAgent && activeSchedule && (
        <Card className="mb-4 shadow-lg" style={cardStyle}>
          <Card.Header style={gradientHeader}>
            <strong>Onboarding Training Agent</strong>
            <Button
              variant="outline-danger"
              className="float-end"
              onClick={() => setShowAgent(false)}
            >Close</Button>
          </Card.Header>
          <Card.Body style={{ height: 600, padding: 0, background: palette.card }}>
            <iframe
              src={`http://localhost:3001/?schedule_id=${activeSchedule.id}`}
              title="Onboarding Agent"
              style={{ width: '100%', height: '100%', border: 'none' }}
            />
          </Card.Body>
        </Card>
      )}
      <link
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.10.5/font/bootstrap-icons.css"
      />
    </div>
  );
}

export default EmployeeDashboard;
