import React from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const ProgramModal = ({ show, onHide, current, setCurrent, submitting, startBuilding, resetForm, palette, gradientHeader }) => (
  <Modal show={show} onHide={onHide} centered>
    <Modal.Header closeButton style={gradientHeader}>
      <Modal.Title style={{ color: palette.primary, fontWeight: 700 }}>Create Program</Modal.Title>
    </Modal.Header>
    <Modal.Body style={{ background: palette.card }}>
      <Form.Group className="mb-3">
        <Form.Label style={{ color: palette.text, fontWeight: 600 }}>Program Title</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter program title"
          value={current.title}
          onChange={e => setCurrent(prev => ({ ...prev, title: e.target.value }))}
          style={{ borderRadius: 8, border: `1px solid ${palette.border}` }}
        />
      </Form.Group>
    </Modal.Body>
    <Modal.Footer style={{ background: palette.card }}>
      <Button variant="success" onClick={startBuilding} disabled={submitting} style={{ borderRadius: 24, fontWeight: 600 }}>
        <i className="bi bi-check-circle me-2"></i>Create
      </Button>
      <Button variant="secondary" onClick={resetForm} style={{ borderRadius: 24, fontWeight: 600 }}>
        <i className="bi bi-x-circle me-2"></i>Cancel
      </Button>
    </Modal.Footer>
  </Modal>
);

export default ProgramModal;
