import React from 'react';
import { Nav } from 'react-bootstrap';
import './sidebar.css'; // <-- add this

/**
 * Standard Bootstrap 5 sidebar
 *
 * Props:
 * - selected: 'programs' | 'schedules'  (controls active tab)
 * - onChange: (key) => void            (optional React handler)
 * - brand: string                      (default "SCOR")
 * - className: string                  (optional extra classes)
 */
const Sidebar = ({
  selected = 'programs',
  onChange,
  brand = 'SCOR',
  className = '',
}) => {
  const handleSelect = (key) => {
    if (onChange) {
      onChange(key);
    } else {
      // Backward compatibility with your current event-based approach
      window.dispatchEvent(new CustomEvent('sidebarTabChange', { detail: key }));
    }
  };

  return (
    <aside
      className={`sidebar bg-white border-end d-flex flex-column align-items-stretch min-vh-100 ${className}`}
      aria-label="Sidebar"
    >
      <div className="sidebar__brand">
        <span className="sidebar__brandText text-success">{brand}</span>
      </div>

      <Nav
        variant="pills"
        className="flex-column px-2 gap-1"
        activeKey={selected}
        onSelect={handleSelect}
      >
        <Nav.Item>
          <Nav.Link eventKey="programs" className="text-start">
            Programs
          </Nav.Link>
        </Nav.Item>

        <Nav.Item>
          <Nav.Link eventKey="schedules" className="text-start">
            Schedules
          </Nav.Link>
        </Nav.Item>
      </Nav>
    </aside>
  );
};

export default Sidebar;
