import React from 'react';
import { Button } from 'react-bootstrap';

const Sidebar = () => (
  <nav style={{ width: 240, background: '#fff', color: '#28a745', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '32px 0', boxShadow: '0 0 24px 0 rgba(40,167,69,0.10)' }}>
    <div style={{ fontWeight: 900, fontSize: '2rem', marginBottom: 32, letterSpacing: 1 }}>SCOR</div>
    <ul style={{ listStyle: 'none', padding: 0, width: '100%' }}>
      <li style={{ margin: '16px 0', width: '100%' }}>
        <Button variant="link" style={{ color: '#28a745', fontWeight: 700, fontSize: '1.1rem', width: '100%' }} onClick={() => window.dispatchEvent(new CustomEvent('sidebarTabChange', { detail: 'programs' }))}>
          Programs
        </Button>
      </li>
      <li style={{ margin: '16px 0', width: '100%' }}>
        <Button variant="link" style={{ color: '#28a745', fontWeight: 700, fontSize: '1.1rem', width: '100%' }} onClick={() => window.dispatchEvent(new CustomEvent('sidebarTabChange', { detail: 'schedules' }))}>
          Schedules
        </Button>
      </li>
    </ul>
  </nav>
);

export default Sidebar;
