import React, { useEffect, useState, useCallback } from 'react';
import { Nav } from 'react-bootstrap';
import './Sidebar.css'; // <-- Add this line

const Sidebar = () => {
  const [activeTab, setActiveTab] = useState('programs');

  const handleTabChange = useCallback((tab) => {
    setActiveTab(tab);
    window.dispatchEvent(new CustomEvent('sidebarTabChange', { detail: tab }));
  }, []);

  // Optional: if something else in the app triggers 'sidebarTabChange', reflect it in the UI
  useEffect(() => {
    const onExternal = (e) => setActiveTab(e.detail);
    window.addEventListener('sidebarTabChange', onExternal);
    return () => window.removeEventListener('sidebarTabChange', onExternal);
  }, []);

  return (
    <div className="sidebar d-flex flex-column border-end" style={{ borderRadius: 0 }}>
      <div className="brand text-white fw-bold text-center py-4 border-bottom" style={{ borderRadius: 0 }}>
        SCOR
      </div>

      <Nav
        className="flex-column px-3 mt-3"
        variant="pills"
        activeKey={activeTab}
        onSelect={(k) => k && handleTabChange(k)}
      >
        <Nav.Link
          eventKey="programs"
          className="sidebar-link fw-semibold fs-6 py-2"
        >
          Programs
        </Nav.Link>

        <Nav.Link
          eventKey="schedules"
          className="sidebar-link fw-semibold fs-6 py-2"
        >
          Schedules
        </Nav.Link>
      </Nav>
    </div>
  );
};

export default Sidebar;
