import React, { useEffect, useState } from 'react';
import { Card, Table, Button, Spinner, Alert, Badge, Form, InputGroup } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_BASE = 'http://localhost:5000/api';

const cardStyle = {
  borderRadius: 16,
  border: 'none',
  boxShadow: '0 2px 16px 0 rgba(0,0,0,0.06)',
  background: '#fff'
};
const gradientHeader = {
  background: '#f7fafc',
  color: '#2d3748',
  borderRadius: '16px 16px 0 0',
  padding: '24px 24px 12px 24px',
  borderBottom: '1px solid #e2e8f0'
};

function EmployeeDashboard() {
  const [email, setEmail] = useState('');
  const [schedules, setSchedules] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showAgent, setShowAgent] = useState(false);
  const [activeSchedule, setActiveSchedule] = useState(null);

  // Fetch schedules for employee
  const fetchSchedules = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await fetch(`${API_BASE}/schedules?email=${encodeURIComponent(email)}`);
      if (!res.ok) throw new Error('Failed to load schedules');
      const data = await res.json();
      setSchedules(Array.isArray(data) ? data : []);
    } catch (err) {
      setError(err.message || 'Failed to load schedules');
      setSchedules([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (email) fetchSchedules();
  }, [email]);

  const handleStartOrResume = (schedule) => {
    setActiveSchedule(schedule);
    setShowAgent(true);
  };

  return (
    <div className="container py-4" style={{ background: 'linear-gradient(120deg, #ffffffff 0%, #ffffffff 100%)', minHeight: '100vh' }}>
      <Card className="mb-4" style={cardStyle}>
        <div style={gradientHeader}>
          <h1 className="mb-0" style={{ fontWeight: 700, letterSpacing: 1 }}>Employee Training Dashboard</h1>
        </div>
        <Card.Body>
          <div className="mb-3">
            <InputGroup style={{ maxWidth: 400 }}>
              <Form.Control
                type="email"
                placeholder="Enter your email to view schedules"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
              {email && (
                <Button variant="outline-secondary" onClick={() => setEmail('')}>Clear</Button>
              )}
            </InputGroup>
          </div>
          {loading ? (
            <Spinner animation="border" />
          ) : error ? (
            <Alert variant="danger">{error}</Alert>
          ) : !email ? (
            <Alert variant="info">Please enter your email to view assigned schedules.</Alert>
          ) : schedules.length === 0 ? (
            <Alert variant="info">No schedules assigned to this email.</Alert>
          ) : (
            <Table striped bordered hover responsive className="shadow-sm" style={{ borderRadius: 12, overflow: 'hidden' }}>
              <thead className="table-dark">
                <tr>
                  <th>Program</th>
                  <th>Start Date</th>
                  <th>End Date</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {schedules.map(s => (
                  <tr key={s.id}>
                    <td>{s.program_title || s.program_id}</td>
                    <td>{s.start_date}</td>
                    <td>{s.end_date}</td>
                    <td>
                      {s.is_finished ? (
                        <Badge bg="success">Completed</Badge>
                      ) : (
                        <Badge bg="warning">Pending</Badge>
                      )}
                    </td>
                    <td>
                      <Button
                        variant="primary"
                        onClick={() => handleStartOrResume(s)}
                      >
                        {s.is_started ? 'Resume' : 'Start'}
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Card.Body>
      </Card>
      {/* Onboarding Agent */}
      {showAgent && activeSchedule && (
        <Card className="mb-4" style={cardStyle}>
          <Card.Header style={gradientHeader}>
            <strong>Onboarding Training Agent</strong>
            <Button
              variant="outline-danger"
              className="float-end"
              onClick={() => setShowAgent(false)}
            >Close</Button>
          </Card.Header>
          <Card.Body style={{ height: 600, padding: 0 }}>
            <iframe
              src={`http://localhost:3001/?schedule_id=${activeSchedule.id}`}
              title="Onboarding Agent"
              style={{ width: '100%', height: '100%', border: 'none' }}
            />
          </Card.Body>
        </Card>
      )}
      <link
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.10.5/font/bootstrap-icons.css"
      />
    </div>
  );
}

export default EmployeeDashboard;
